<?phpclass Products extends DatabaseObject {    protected static $table_name = "products";    protected static $db_fields = array('id', 'name', 'description', 'price_party', 'price25', 'price32', 'price40', 'price45', 'gr_25cm', 'gr_32cm', 'gr_40cm', 'gr_45cm', 'gr_party', 'stock', 'image', 'thumb', 'categorie', 'promotie', 'resigilate', 'lichidari', 'rate_vote', 'rate_number', 'avarage');    public $id;    public $name;    public $description;    public $price_party;    public $price25;    public $price32;    public $price40;    public $price45;    public $gr_25cm;    public $gr_32cm;    public $gr_40cm;    public $gr_45cm;    public $gr_party;    public $stock;    public $image;    public $thumb;    public $rate_vote;    public $rate_number;    public $avarage;    public $errors = array();    public function getProducts() {        return self::find_by_views();    }        public function findProducts($searchString)    {               $searchSQL = "SELECT * FROM products WHERE name like '%".$searchString."%' OR description like '%".$searchString."%'";              return self::find_by_sql($searchSQL);    }        public static function getPID($id) {        return self::find_by_id($id);    }}class Procesoare extends DatabaseObject {    protected static $table_name = "procesoare";    protected static $db_fields = array('id', 'name', 'description', 'price_party', 'price25', 'price32', 'price40', 'price45', 'gr_25cm', 'gr_32cm', 'gr_40cm', 'gr_45cm', 'gr_party', 'stock', 'image', 'thumb', 'categorie', 'promotie', 'resigilate', 'lichidari', 'rate_vote', 'rate_number', 'avarage');    public $id;    public $name;    public $description;    public $price_party;    public $price25;    public $price32;    public $price40;    public $price45;    public $gr_25cm;    public $gr_32cm;    public $gr_40cm;    public $gr_45cm;    public $gr_party;    public $stock;    public $image;    public $thumb;    public $rate_vote;